package org.jetlinks.community.network.manager.enums;

import org.jetlinks.community.network.manager.entity.CertificateEntity;
import org.jetlinks.community.network.security.DefaultCertificate;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertNotNull;

class CertificateTypeTest {

    @Test
    void PFX() {
        DefaultCertificate defaultCertificate = new DefaultCertificate("test","test");

        CertificateEntity.CertificateConfig config = new CertificateEntity.CertificateConfig();
        config.setKeystoreBase64("");
        config.setKeystorePwd("lvchuan");

        config.setTrustKeyStoreBase64("");
        config.setTrustKeyStorePwd("lvchuan");
        DefaultCertificate init = CertificateType.JKS.init(defaultCertificate, config);
        assertNotNull(init);
        DefaultCertificate init1 = CertificateType.PFX.init(defaultCertificate, config);
        assertNotNull(init1);
    }

    @Test
    void PEM() {
        DefaultCertificate defaultCertificate = new DefaultCertificate("test","test");
        CertificateEntity.CertificateConfig config = new CertificateEntity.CertificateConfig();

        config.setKeystoreBase64("");
        config.setKeystorePwd("changgou");
        //config.setTrustKeyStoreBase64(s);
        config.setTrustKeyStoreBase64("");
        config.setTrustKeyStorePwd("changgou");
        DefaultCertificate init = CertificateType.PEM.init(defaultCertificate, config);
        assertNotNull(init);
    }

}